import React, { Component } from 'react'

export class DropdownMenu extends Component {
    constructor(){
        super();

        this.state = {
              displayMenu: false,
            };

         this.showDropdownMenu = this.showDropdownMenu.bind(this);
         this.hideDropdownMenu = this.hideDropdownMenu.bind(this);

       };

       showDropdownMenu(event) {
           event.preventDefault();
           this.setState({ displayMenu: true }, () => {
           document.addEventListener('click', this.hideDropdownMenu);
           });
         }

         hideDropdownMenu() {
           this.setState({ displayMenu: false }, () => {
             document.removeEventListener('click', this.hideDropdownMenu);
           });

         }
    render() {
        return (
            <div  className="dropdown" style = {{background:"red",width:"200px"}} >
         <div className="button" onClick={this.showDropdownMenu}> Choose Age Group </div>

          { this.state.displayMenu ? (
          <ul>
         <li><a className="active" href="#Create Page">17 below</a></li>
         <li><a href="#Manage Pages">18 - 24</a></li>
         <li><a href="#Create Ads">25 - 34</a></li>
         <li><a href="#Manage Ads">35 - 44</a></li>
         <li><a href="#Activity Logs">45 - 54</a></li>
         <li><a href="#Setting">55 - 64</a></li>
          </ul>
        ):
        (
          null
        )
        }

       </div>
        )
    }
}

export default DropdownMenu
