import React, { Component } from 'react'
import { VictoryPie, VictoryLabel } from 'victory'

export class DropdownMenu extends Component {
    constructor(){
        super();

        this.state = {
              displayMenu: false,
            };

         this.showDropdownMenu = this.showDropdownMenu.bind(this);
         this.hideDropdownMenu = this.hideDropdownMenu.bind(this);

       };

        showDropdownMenu(event) {
           event.preventDefault();
           this.setState({ displayMenu: true }, () => {
           document.addEventListener('click', this.hideDropdownMenu);
           });
         }

        hideDropdownMenu() {
            this.setState({ displayMenu: false }, () => {
                document.removeEventListener('click', this.hideDropdownMenu);
           });

         }
    render() {
        function handleClick(e) {
            e.preventDefault();
            <div id = "pie2">
            <VictoryPie
                data={[
                    { x: "TV", y: 0.10 },
                    { x: "Online Ads", y: 0.21 },
                    //{ x: "Appstore", y: 1216 },
                    { x: "Web", y: 0.27 },
                    { x: "Recommendation", y: 0.16 }

                ]}
                colorScale={["tomato", "navy", "gold", "cyan" ]}
                //cornerRadius={({ datum }) => datum.y * 5}
                height={300}
                width={500}
                padAngle={({ datum }) => datum.y}
                padding={{ top: 80, bottom: 60, left: -95 }}
            />
            <VictoryLabel text="clustering discounted sales by channel" textAnchor="left"/>
            </div>
          }
        return (
            <div  className="dropdown" style = {{background:"red",width:"200px"}} >
            <div className="button" onClick={this.showDropdownMenu}> Choose Age Group </div>

            { this.state.displayMenu ? (
            <ul>
            <li><a className="active" href="#17" onClick={handleClick}>17 below</a></li>
            <li><a href="#18">18 - 24</a></li>
            <li><a href="#25">25 - 34</a></li>
            <li><a href="#35">35 - 44</a></li>
            <li><a href="#45">45 - 54</a></li>
            <li><a href="#55">55 - 64</a></li>
            </ul>
            ):
            (
                null
            )
        }

       </div>
        )
    }
}

export default DropdownMenu
